CREATE SCHEMA DXRADMIN;

CREATE TABLE DXRADMIN.static_page (
    static_page_id VARCHAR(255) NOT NULL,
    static_content VARCHAR(32000),
    PRIMARY KEY (static_page_id)
);

CREATE TABLE DXRADMIN.faq_type (
    faq_type_id VARCHAR(255) NOT NULL,
    faq_type VARCHAR(255),
    faq_type_description VARCHAR(512),
    PRIMARY KEY (faq_type_id)
);

CREATE TABLE DXRADMIN.faq_info (
    faq_info_id VARCHAR(255) NOT NULL,
    faq_info_question VARCHAR(255),
    faq_info_answer VARCHAR(10000),
    faq_type_id VARCHAR(255),
    PRIMARY KEY (faq_info_id),
    FOREIGN KEY (faq_type_id) REFERENCES faq_type (faq_type_id)
);

CREATE TABLE DXRADMIN.subscription_status (
	subscription_status_id VARCHAR(255),
	status_title VARCHAR(255),
	status_code VARCHAR(255),
	PRIMARY KEY (subscription_status_id)
); 

CREATE TABLE DXRADMIN.subscription_info(
	subscription_info_id VARCHAR(255),
	company_name VARCHAR(255),
	address VARCHAR(255),
	contact_no VARCHAR(255),
	email VARCHAR(255),
    agreed_terms VARCHAR(255),
    date_time VARCHAR(255),
    subscription_status VARCHAR(255),
    is_human VARCHAR(255),
    is_agree VARCHAR(255),
    subscriber_name VARCHAR(255),
    subscriber_mail VARCHAR(255),
	PRIMARY KEY (subscription_info_id),
    FOREIGN KEY (subscription_status) REFERENCES subscription_status (subscription_status_id));

CREATE TABLE DXRADMIN.subscription_company_catagory (
	subscription_company_category_id BIGINT generated always as identity,
	subscription_info VARCHAR(255),
	company_category_id_list VARCHAR(1024),
	PRIMARY KEY (subscription_company_category_id),
    FOREIGN KEY (subscription_info) REFERENCES subscription_info (subscription_info_id)
);

CREATE TABLE DXRADMIN.company_category (
	company_category_id VARCHAR(255),
	category_name VARCHAR(255),
	category_code VARCHAR(255),
	category_description VARCHAR(1024),
    PRIMARY KEY(company_category_id)
);

CREATE TABLE DXRADMIN.inquiry_info (
	inquiry_info_id VARCHAR(255),
	company_name VARCHAR(255),
	person_name VARCHAR(255),
	contact_no VARCHAR(255),
    email VARCHAR(255),
    inquiry_title VARCHAR(255),
    inquiry_detail VARCHAR(255),
    agreed_terms VARCHAR(255),
    date_time VARCHAR(255),
    is_human VARCHAR(255),
    is_agree VARCHAR(255),
	PRIMARY KEY (inquiry_info_id)
);

CREATE TABLE DXRADMIN.inquiry_response (
	inquiry_response_id VARCHAR(255),
	response VARCHAR(10000),
	date_time VARCHAR(255),
    inquiry_info_id VARCHAR(255),
    PRIMARY KEY(inquiry_response_id),
	FOREIGN KEY (inquiry_info_id) REFERENCES inquiry_info (inquiry_info_id)
);

CREATE TABLE DXRADMIN.company_info (
    company_info_id VARCHAR(255) NOT NULL,
    company_name VARCHAR(255),
    company_address VARCHAR(255),
    company_email VARCHAR(255),
    company_contact VARCHAR(255),
    subscription_id VARCHAR(255),
    PRIMARY KEY (company_info_id),
    FOREIGN KEY (subscription_id) REFERENCES subscription_info (subscription_info_id)
);

CREATE TABLE DXRADMIN.user_identification (
    user_identification_id VARCHAR(255) NOT NULL,
    user_id VARCHAR(255),
    user_auth VARCHAR(255),
    one_time_access_flag VARCHAR(255),
    PRIMARY KEY (user_identification_id)
);

CREATE TABLE DXRADMIN.user_info (
    user_info_id VARCHAR(255) NOT NULL,
    user_name VARCHAR(255),
    user_email VARCHAR(255),
    user_contact VARCHAR(255),
    user_address VARCHAR(255),
    department_title VARCHAR(255),
    user_company_id VARCHAR(255),
    user_auth_id VARCHAR(255),
    PRIMARY KEY (user_info_id),
--     FOREIGN KEY (user_company_id) REFERENCES company_info (company_info_id),
    FOREIGN KEY (user_auth_id) REFERENCES user_identification (user_identification_id)
);

CREATE TABLE DXRADMIN.user_access_info (
    user_access_info_id VARCHAR(255) NOT NULL,
    menu_access_info VARCHAR(32000),
    PRIMARY KEY (user_access_info_id),
    FOREIGN KEY (user_identification) REFERENCES user_identification (user_identification_id)
);





CREATE TABLE DXRADMIN.access_def (
    access_def_id VARCHAR(255) NOT NULL,
    access_title VARCHAR(255),
    access_code VARCHAR(255),
    access_description VARCHAR(255),
    PRIMARY KEY (access_def_id)
);

CREATE TABLE DXRADMIN.role_def (
    role_def_id VARCHAR(255) NOT NULL,
    role_name VARCHAR(255),
    role_code VARCHAR(255),
    role_description VARCHAR(255),
    PRIMARY KEY (role_def_id)
);

CREATE TABLE DXRADMIN.role_access_def (
    role_access_def_id VARCHAR(255) NOT NULL,
    role_id VARCHAR(255),
    access_def_list VARCHAR(500),
    PRIMARY KEY (role_access_def_id)
);

CREATE TABLE DXRADMIN.menu_def (
    menu_id VARCHAR(255) NOT NULL,
    menu_title VARCHAR(255),
    menu_url VARCHAR(255),
    parent_segment VARCHAR(255),
    menu_group_name VARCHAR(255),
    entity_type_id VARCHAR(255),
    PRIMARY KEY (menu_id)
);

CREATE TABLE DXRADMIN.menu_role_def (
    menu_role_def_id VARCHAR(255) NOT NULL,
    menu_id VARCHAR(255),
    role_def_list VARCHAR(255),
    PRIMARY KEY (menu_role_def_id)
);

CREATE TABLE DXRADMIN.company_admin_access (
    company_admin_access_id VARCHAR(255) NOT NULL,
    company_admin_access VARCHAR(32000),
    PRIMARY KEY (company_admin_access_id)
);







